import Button from "../../Button";

const Learn_Middle_Image = ({
  topic,
  title,
  cover,
  describe,
  orderLink,
  inventoryLink,
  numbers,
  right,
}) => {
  return (
    <section
      className={`min-h-screen bg-slate-100 flex ${
        right ? "lg:flex-row-reverse" : "lg:flex-row"
      } flex-col`}
    >
      <div className="lg:w-2/6 text-slate-900">
        <h2>{topic}</h2>
        <h3 className="sm:text-4xl text-2xl">{title}</h3>
        <p>{describe}</p>
        <Button to={orderLink} label="Order Now" outline="dark" />
        <Button to={inventoryLink} label="View Inventory" light />
      </div>
      <div className="lg:w-/6 relative">
        {cover.endsWith(".mp4") || cover.endsWith(".webm") ? (
          <video autoPlay muted loop className="bg-fullobject size-full">
            <source src={cover} type={`video/${cover.split(".").pop()}`} />
            Your browser does not support the video tag.
          </video>
        ) : (
          <img src={cover} alt={title} className="size-full bg-fullobject" />
        )}
        <div className="absolute grid gap-x-4 mx-auto">
          {numbers.map((item) => {
            return (
              <div>
                <h4>
                  {item.value}
                  <span className="text-sm">{item.unit}</span>
                </h4>
                <p>{item.describe}</p>
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
};

export default Learn_Middle_Image;
