import { Splide, SplideSlide } from "@splidejs/react-splide";

const Learn_Powertrain = (props) => {
  const { car } = props;
  const carPowerTrain = car.powerTrain;
  if (car.powerTrain) {
    return (
      <section>
        <h2>Electric Powertrain</h2>
        <p>{carPowerTrain.describe}</p>
        <Splide
          aria-label="Tesla Hero Slider"
          options={{
            type: "fade",
            rewind: true,
            autoplay: true,
            arrows: false,
            pagination: false,
          }}
          onMounted={(splide) => {
            const pagination = document.querySelector(".splide__pagination");
            carPowerTrain.editions.forEach((_, index) => {
              const button = document.createElement("button");
              button.classList.add("splide__pagination__bullet");
              button.setAttribute("aria-label", `Slide ${index + 1}`);
              pagination.appendChild(button);
            });
            splide.sync(".splide__pagination__bullet");
          }}
        >
          {carPowerTrain.editions.map((model) => {
            return (
              <SplideSlide>
                <img src={model.image} alt={model.title} />
              </SplideSlide>
            );
          })}
        </Splide>
        <div className="flex">
          {carPowerTrain.editions.map((model) => {
            return (
              <div className="w-1/2">
                <h5>{model.title}</h5>
                <p>{model.describe}</p>
                <div className="">
                  <h4>
                    {model.acceleration}
                    <span>s</span>
                  </h4>
                  <h5>0-100 km/h</h5>
                </div>
                <div className="">
                  <h4>
                    {model.range}
                    <span>km</span>
                  </h4>
                  <h5>Range (EPA est.)</h5>
                </div>
              </div>
            );
          })}
        </div>
      </section>
    );
  }
};

export default Learn_Powertrain;
