import { Splide, SplideSlide, SplidePagination } from "@splidejs/react-splide";

const CustomPagination = ({ pagination }) => {
  return (
    <div className="flex">
      {pagination.map((_, index) => {
        return (
          <div key={index} className="w-1/2 pageButton">
            <h5>{carPowerTrain.editions[index].title}</h5>
            <p>{carPowerTrain.editions[index].describe}</p>
            <div className="">
              <h4>
                {carPowerTrain.editions[index].acceleration}
                <span>s</span>
              </h4>
              <h5>0-100 km/h</h5>
            </div>
            <div className="">
              <h4>
                {carPowerTrain.editions[index].range}
                <span>km</span>
              </h4>
              <h5>Range (EPA est.)</h5>
            </div>
          </div>
        );
      })}
    </div>
  );
};

const Learn_Powertrain = (props) => {
  const { car } = props;
  const carPowerTrain = car.powerTrain;
  if (car.powerTrain) {
    return (
      <section>
        <h2>Electric Powertrain</h2>
        <p>{carPowerTrain.describe}</p>
        <Splide
          aria-label="Tesla Hero Slider"
          options={{
            type: "fade",
            rewind: true,
            autoplay: true,
            arrows: false,
          }}
        >
          {carPowerTrain.editions.map((model) => {
            return (
              <SplideSlide>
                <img src={model.image} alt={model.title} />
              </SplideSlide>
            );
          })}

          <div className="flex">
            {carPowerTrain.editions.map((model) => {
              return (
                <div className="w-1/2 pageButton">
                  <h5>{model.title}</h5>
                  <p>{model.describe}</p>
                  <div className="">
                    <h4>
                      {model.acceleration}
                      <span>s</span>
                    </h4>
                    <h5>0-100 km/h</h5>
                  </div>
                  <div className="">
                    <h4>
                      {model.range}
                      <span>km</span>
                    </h4>
                    <h5>Range (EPA est.)</h5>
                  </div>
                </div>
              );
            })}
          </div>
        </Splide>
      </section>
    );
  }
};

export default Learn_Powertrain;
