import React, { useState } from 'react';

const Home = () => {
    const [isDark, setDark] = useState(false);

    const toggleDarkMode = () => {
        setDark(!isDark);
    };

    useEffect(() => {
        if (isDark) {
            document.documentElement.classList.add('dark');
        } else {
            document.documentElement.classList.remove('dark');
        }
    }, [isDark]);

    return (
        <main className="p-5">
            <h1 className={`text-white ${isDark ? 'dark:text-black' : ''}`}>
                Hello world!
            </h1>
            <button
                id="darkTheme"
                className="mt-8 rounded-md bg-black hover:bg-slate-800 dark:bg-white dark:hover:bg-slate-200 text-white px-6 py-2"
                onClick={toggleDarkMode}
            >
                Dark
            </button>
        </main>
    );
};

export default Home;
