import { events } from "../constants";
import { useParams } from "react-router-dom";

const Event = () => {
  const { event } = useParams();
  const theEvent = events.find(
    (e) =>
      e.title.replace(/\s+/g, "-").toLowerCase() ===
      event.replace(/\s+/g, "-").toLowerCase()
  );
  return (
    <>
      <div
        className="h-96 bg-fullcenter"
        style={{ backgroundImage: "url(" + Background + ")" }}
      ></div>
      <section className="pagecenter-x pb-40">
        <div className="flex justify-between max-md:flex-col-reverse">
          <div className="md:w-7/12">
            <h2 className="title mb-2 max-md:hidden">Near Chicago, Illinois</h2>
            <p className="text-sm text-slate-200 mt-5">
              Can't find an event you're looking for?{" "}
              <Link to="/communication-preferences/subscribe" className="link">
                Receive notifications for future events.
              </Link>
            </p>
            <div className="mt-24 grid gap-y-12"></div>
          </div>
          <div className="md:w-4/12">
            <h2 className="mb-2 md:hidden">Near Chicago, Illinois</h2>
            <Field
              icon="fa-magnifying-glass"
              id="location"
              placeholder="Search by location"
            />
          </div>
        </div>
      </section>
    </>
  );
};

export default Event;
