import React from "react";

const Padding_Middle = ({ title, cover, describe, right = false }) => {
  return (
    <section
      className={`min-h-screen padding flex md:${
        right ? "flex-row-reverse" : "flex-row"
      }`}
    >
      <div className="md:w-3/4">
        {cover.endsWith(".mp4") || cover.endsWith(".webm") ? (
          <video autoPlay loop controls className="bg-fullobject w-full">
            <source src={cover} type={`video/${cover.split(".").pop()}`} />
            Your browser does not support the video tag.
          </video>
        ) : (
          <img src={cover} alt={title} className="bg-fullobject w-full" />
        )}
      </div>
      <div className="md:w-3/4">
        <div className="pt-12">
          <h2>{title}</h2>
          <p className="md:text-sm">{describe}</p>
        </div>
      </div>
    </section>
  );
};

export default Padding_Middle;
