import { Splide, SplideSlide } from "@splidejs/react-splide";

const Learn_Powertrain = (props) => {
  const { car } = props;
  const carPowerTrain = car.powerTrain;

  const splide = new Splide(".splide");

  splide.on("pagination:mounted", function (data) {
    data.list.classList.add("pagiButton");

    data.items.forEach(function (item) {
      item.button.textContent = String(item.page + 1);
    });
  });

  splide.mount();

  if (car.powerTrain) {
    return (
      <section>
        <h2>Electric Powertrain</h2>
        <p>{carPowerTrain.describe}</p>
        <Splide
          aria-label="Tesla Hero Slider"
          options={{
            type: "fade",
            rewind: true,
            autoplay: true,
            arrows: false,
          }}
        >
          {carPowerTrain.editions.map((model) => {
            return (
              <SplideSlide>
                <img src={model.image} alt={model.title} />
              </SplideSlide>
            );
          })}
        </Splide>

        <div className="flex">
          {carPowerTrain.editions.map((model) => {
            return (
              <div className="w-1/2 pagiButton">
                <h5>{model.title}</h5>
                <p>{model.describe}</p>
                <div className="">
                  <h4>
                    {model.acceleration}
                    <span>s</span>
                  </h4>
                  <h5>0-100 km/h</h5>
                </div>
                <div className="">
                  <h4>
                    {model.range}
                    <span>km</span>
                  </h4>
                  <h5>Range (EPA est.)</h5>
                </div>
              </div>
            );
          })}
        </div>
      </section>
    );
  }
};

export default Learn_Powertrain;
