import React from "react";
import { Link } from "react-router-dom";

const Form = ({ title = null, form }) => {
  const isMobile = window.matchMedia(
    "only screen and (max-width: 767px)"
  ).matches;
  return (
    <section className="padding">
      <div className="mx-auto max-w-[1000px] flex md:gap-x-24 max-md:flex-col-reverse">
        <div>
          <h2 className="title">{title}</h2>
        </div>
        <form>
          {form.map((item, index) => {
            const id = item.label.replace(/\s+/g, "-").toLowerCase();
            return (
              <div key={index} className={`${index > 0 && "mt-6"}`}>
                <label
                  htmlFor={id}
                  className="text-slate-200 md:text-sm text-xs font-bold"
                >
                  {item.label}
                </label>
                <div className="mt-1">
                  {item.type === "textarea" ? (
                    <textarea className="field min-h-[120px]"></textarea>
                  ) : item.type === "select" ? (
                    <select className="field">
                      {item.options.map((option, index) => {
                        if (item.label === "Contact Preference") {
                          if (isMobile) {
                            if (index === 0) {
                              return (
                                <option key={index} selected>
                                  {option}
                                </option>
                              );
                            }
                          } else {
                            if (index === 1) {
                              return (
                                <option key={index} selected>
                                  {option}
                                </option>
                              );
                            }
                          }
                        } else {
                          return <option key={index}>{option}</option>;
                        }
                      })}
                    </select>
                  ) : (
                    <input id={id} type={item.type} className="field" />
                  )}
                </div>
              </div>
            );
          })}
          <button className="button button-sky w-full mt-12" type="submit">
            Submit
          </button>
          <div className="mt-8 text-center px-5 text-xs text-slate-200">
            <p>
              I consent to be contacted about Tesla products, including through
              automated calls or texts.
            </p>
            <p>
              I understand and agree to{" "}
              <Link to="/" className="link">
                Teslaâ€™s Privacy Notice
              </Link>{" "}
              and{" "}
              <Link to="/" className="link">
                Terms of Use
              </Link>
              .
            </p>
          </div>
        </form>
      </div>
    </section>
  );
};

export default Form;
