import React from "react";
import { vehicles } from "../../../constants";

const Learn_Amenities = (props) => {
  const { car } = props;
  const carAmenities = vehicles.find(
    (vehicle) => vehicle.model === car.model
  ).amenities;

  return (
    <section className="lg:px-10 py-5 mx-auto max-w-[1400px]">
      {carAmenities.map((item) => {
        return (
          <div className="flex justify-between sm:flex-row flex-col sm:px-5 py-1">
            <div className="sm:w-1/2 flex items-center">
              {item.image.endsWith(".mp4") || item.image.endsWith(".webm") ? (
                <video autoPlay muted loop className="">
                  <source
                    src={item.image}
                    type={`video/${item.image.split(".").pop()}`}
                  />
                  Your browser does not support the video tag.
                </video>
              ) : (
                <img
                  src={item.image}
                  alt={item.title}
                  className=" bg-fullobject"
                />
              )}
            </div>
            <div className="sm:w-1/2 flex items-center">
              <div className="sm:px-5 sm:pt-0 pt-5">
                <h3>{item.title}</h3>
                <p>{item.describe}</p>
              </div>
            </div>
          </div>
        );
      })}
    </section>
  );
};

export default Learn_Amenities;
