import { vehicles } from "../../constants";
import { useEffect } from "react";

const Demo_Models = () => {
  const [showModel, setShowModel] = useEffect("demo_modelx");

  const handleClick = () => {};

  useEffect(() => {
    const intervalId = setInterval(() => {
      for (let i = 0; i < videoSlide.length; i++) {
        if (videoSlide[i] && videoSlide[i].classList.contains("is-visible")) {
          const video = videoSlide[i].querySelector("video");
          if (video && video.paused && document.hasFocus()) {
            video.play();
          }
        } else {
          const video = videoSlide[i].querySelector("video");
          if (video) {
            video.pause();
            video.currentTime = 0;
          }
        }
      }
    }, 50);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <section className="flex max-md:flex-col">
      <div className="md:w-4/12 flex flex-col gap-2">
        {vehicles.map((item, index) => {
          const id = item.model.replace(/\s+/g, "-").toLowerCase();
          return (
            <button
              key={index}
              data-model={`demo_${id}`}
              className="rounded border border-slate-100 text-sm text-center py-3"
            >
              <h3>{item.model}</h3>
            </button>
          );
        })}
      </div>
      <div className="md:w-8/12">
        {vehicles.map((item, index) => {
          const id = item.model.replace(/\s+/g, "-").toLowerCase();
          return (
            <div
              key={index}
              data-model={`demo_${id}`}
              className="flex-centralizer"
            >
              <img
                src={item.trsModel}
                alt="Transparent Model"
                data-model-demo={`demo_${id}`}
                className="demoDriveModel"
              />
            </div>
          );
        })}
      </div>
    </section>
  );
};

export default Demo_Models;
