import { useEffect, useState } from "react";

const Select_Price = ({ car }) => {
  const [showModel, setShowModel] = useState(0);
  useEffect(() => {
    const interval = setInterval(() => {
      setShowModel((showModel) => (showModel + 1) % car.editions.lenght);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  const demoButton = Array.from(
    document.getElementsByClassName("demoDriveButton")
  );

  demoButton.forEach((button, index) => {
    button.addEventListener("click", () => {
      setShowModel(index);
    });
  });

  return (
    <section className="text-start">
      {car.editions.map((item, index) => {
        return (
          <>
            {item.title && <h5>{item.title}</h5>}
            <button
              key={index}
              onClick={() => setShowModel(index)}
              className={`demoDriveButton rounded w-full px-3 py-5 border border-slate-200 transition-all ${
                showModel === index && "bg-slate-200 text-slate-950"
              } text-xs py-3 max-md:min-h-[100px] flex-seperate`}
            >
              <span>{item.label}</span>
              <span>${item.price}</span>
            </button>
          </>
        );
      })}
    </section>
  );
};

export default Select_Price;
