import React from "react";

const Form = ({ title = null, form }) => {
  return (
    <section className="padding">
      <div className="mx-auto max-w-[900px] flex md:gap-x-52 max-md:flex-col-reverse">
        <div>
          <h2 className="title">{title}</h2>
        </div>
        <form>
          {form.map((item, index) => {
            const id = item.label.replace(/\s+/g, "-").toLowerCase();
            return (
              <div key={index} className={`${index > 0 && "mt-5"}`}>
                <label
                  htmlFor={id}
                  className="text-slate-200 text-xs font-bold"
                >
                  {item.label}
                </label>
                <input id={id} type="text" className="field mt-1" />
              </div>
            );
          })}
        </form>
      </div>
    </section>
  );
};

export default Form;
