import { Link } from "react-router-dom";

const Dropdown = ({
  prods = false,
  links = false,
  discover = false,
  className = null,
}) => {
  return (
    <div
      className={`absolute top-[-500px] left-0 w-full bg-white shadow-md transition-all duration-300 ${className} ${
        dropdownOpen ? "top-0" : "top-[-500px]"
      }`}
    >
      <ul className="list-none p-4">
        {prods.map((prod, index) => (
          <li key={index} className="mb-2">
            <Link to={prod.href} className="text-slate-950 hover:underline">
              {prod.label}
            </Link>
          </li>
        ))}
      </ul>
      <ul className="list-none p-4 border-t border-slate-200">
        {links.map((link, index) => (
          <li key={index} className="mb-2">
            <Link to={link.href} className="text-slate-950 hover:underline">
              {link.label}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dropdown;
