import { Splide, SplideSlide } from "@splidejs/react-splide";
import React, { useEffect, useRef } from "react";

const Free_slider = ({ prod, dark = false }) => {
  const sliderRef = useRef(null);

  useEffect(() => {
    const intervalId = setInterval(() => {
      const videoSlide =
        sliderRef.current.splide.Components.Slides.getSlides()[0];
      if (videoSlide && videoSlide.isVisible()) {
        const video = videoSlide.node.getElementById("cover");
        if (video && !video.playing && document.hasFocus()) {
          video.play();
        }
      }
    }, 50);

    return () => clearInterval(intervalId);
  }, [prod]);

  const handleTabClick = (tabId) => {
    const videoSlide =
      sliderRef.current.splide.Components.Slides.getSlides()[0];
    const newSlide = videoSlide.slide.dataset.tabcontent === tabId;
    if (videoSlide && !newSlide) {
      const previousVideoSlide =
        videoSlide.slide.previousElementSibling ||
        videoSlide.slide.nextElementSibling;
      if (
        previousVideoSlide &&
        previousVideoSlide.classList.contains("is-visible")
      ) {
        const video = previousVideoSlide.getElementById("cover");
        if (video) {
          video.pause();
          video.currentTime = 0;
        }
        previousVideoSlide.classList.remove("is-visible", "is-active");
      }
    }
    if (videoSlide && newSlide) {
      videoSlide.slide.classList.add("is-visible", "is-active");
      const video = videoSlide.slide.getElementById("cover");
      if (video) {
        video.play();
      }
    }
  };

  return (
    <section className={`padding ${!dark && "bg-slate-50 text-slate-950"}`}>
      <Splide
        ref={sliderRef}
        aria-label="Power Train"
        options={{
          type: "fade",
          rewind: true,
          autoplay: true,
          arrows: false,
          pagination: false,
        }}
      >
        {prod.map((ability, index) => (
          <SplideSlide
            data-splide-interval={ability.duration}
            data-tabcontent={`tabAutopilot_${index}`}
            key={index}
          >
            {ability.cover.endsWith(".mp4") ||
            ability.cover.endsWith(".webm") ? (
              <video
                muted
                loop
                className="bg-fullobject w-full rounded"
                id="cover"
              >
                <source
                  src={ability.cover}
                  type={`video/${ability.cover.split(".").pop()}`}
                />
                Your browser does not support the video tag.
              </video>
            ) : (
              <img
                src={ability.cover}
                alt={ability.title}
                className="bg-fullobject w-full rounded"
                id="cover"
              />
            )}
          </SplideSlide>
        ))}
      </Splide>

      <div className="flex padding-x py-5 gap-x-8 mx-auto">
        {prod.map((ability, index) => (
          <div
            className={`border-t-2 px-1 pt-3 ${
              dark ? "border-slate-100" : "border-slate-900"
            }`}
            data-tabbutton={`tabAutopilot_${index}`}
            key={index}
            onClick={() => handleTabClick(`tabAutopilot_${index}`)}
          >
            <h5 className={`${ability.describe ? "text-xl" : "text-sm"}`}>
              {ability.title}
            </h5>
            {ability.describe && (
              <p className="mt-3 text-xs">{ability.describe}</p>
            )}
          </div>
        ))}
      </div>
    </section>
  );
};

export default Free_slider;
