import React from "react";

const Specs = ({ title, cover, items }) => {
  return (
    <section className="min-h-screen flex md:flex-row flex-col padding-y md:px-20 px-12">
      <div className="md:w-1/2">
        <img src={cover} alt={title} className="h-auto bg-fullobject" />
      </div>
      <div className="md:w-1/2 h-full">
        <h2 className="md:text-3xl text-xl">
          {title}
          <span className="font-thin"> Specs</span>
        </h2>
        <div className="grid grid-cols-2 gap-5 pt-8">
          {items.map((item, index) => {
            return (
              <div className="md:text-sm w-1/2" key={index}>
                <hr className="pb-3 border-slate-500 w-8" />
                <h4>{item.title}</h4>
                <ul className="font-thin">
                  {item.details.map((item, index) => {
                    return <li key={index}>{item}</li>;
                  })}
                </ul>
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
};

export default Specs;
