import React from "react";
import { vehicles } from "../../../constants";
import Learn_InterSlide from "../../Portal";
import { Splide } from "@splidejs/react-splide";

const Learn_Inter = (props) => {
  const { car } = props;
  const carInter = vehicles.find((vehicle) => vehicle.model === car.model)
    .interior.slides;

  return (
    <div>
      <Splide
        aria-label={`${car.model} hero slider`}
        options={{
          type: "fade",
          rewind: true,
          autoplay: true,
          arrows: false,
        }}
      >
        {carSlides.map((slide) => {
          return (
            <Learn_InterSlide
              image={slide.image}
              title={slide.title}
              describe={slide.describe}
            />
          );
        })}

        <div className="absolute w-full text-center top-[90%] z-10 md:text-sm text-[9px]">
          <p>{car.saving}</p>
          <Link className="link">Learn about est. gas savings.</Link>
        </div>
        <div className="md:block hidden splide__progress">
          <div className="splide__progress__bar" />
        </div>
      </Splide>
    </div>
  );
};

export default Learn_Inter;
