import React from "react";

const Learn_Bgsection = ({ car, seperate = false }) => {
  return (
    <section className="h-[120vh] ">
      {seperate ? (
        <div className="size-full padding">
          <img
            src={car.cover}
            alt={car.title}
            className="bg-fullobject rounded mx-auto max-w-[900px]"
          />
          <div className="max-w-[800px] p-2">
            <h3 className="title">{car.title}</h3>
            <p className="mt-5">{car.describe}</p>
          </div>
        </div>
      ) : (
        <div
          className="size-full flex flex-col justify-end bg-fullcenter padding"
          style={{ backgroundImage: "url(" + car.cover + ")" }}
        >
          <div className="max-w-[800px] p-2">
            <h3 className="title">{car.title}</h3>
            <p className="mt-5">{car.describe}</p>
          </div>
        </div>
      )}
    </section>
  );
};

export default Learn_Bgsection;
