import React from "react";
import { Fadein } from "../Portal";
import { Link } from "react-router-dom";

const Specs = ({ topic = false, title, describe = false, cover, items }) => {
  return (
    <section className="md:min-h-screen flex md:flex-row flex-col padding-x">
      <div className="md:w-1/2 overflow-hidden flex items-center">
        <img src={cover} alt={title} className="bg-fullobject" />
      </div>
      <div className="md:w-1/2 padding">
        <h3>{topic}</h3>
        <h2 className="title">
          {title}
          <span className="font-normal"> Specs</span>
        </h2>
        {describe && <p className="pt-5 md:text-sm text-xs">{describe}</p>}
        <div className="flex gap-x-12">
          <div className="pt-10">
            {items.fisrtCol.map((item, index) => {
              return (
                <Fadein>
                  <div className="md:text-sm pt-5" key={index}>
                    <hr className="pb-3 border-slate-500 w-8" />
                    <h4>{item.title}</h4>
                    <ul className="text-slate-200 leading-5 text-xs">
                      {item.details.map((item, index) => {
                        return <li key={index}>{item}</li>;
                      })}
                    </ul>
                  </div>
                </Fadein>
              );
            })}
          </div>
          <div className="pt-10">
            {items.secondCol.map((item, index) => {
              return (
                <Fadein>
                  <div className="md:text-sm pt-5" key={index}>
                    <hr className="pb-3 border-slate-500 w-8" />
                    <h4>{item.title}</h4>
                    <ul className="text-slate-200 leading-5 text-xs">
                      {item.details.map((item, index) => {
                        return <li key={index}>{item}</li>;
                      })}
                    </ul>
                  </div>
                </Fadein>
              );
            })}
          </div>
        </div>
      </div>
      <p className="absolute bottom-[1vh] text-center text-xs">
        *Based on average diesel prices in California between February 2022–July
        2022 and typical electric rates.
        <br /> Certain high data usage vehicle features require at least
        Standard Connectivity, including maps, navigation and voice commands.
        Access to features that use cellular data and third-party licenses are
        subject to change.{" "}
        <Link to="/">
          Learn more about Standard Connectivity and any limitations
        </Link>
        .
      </p>
    </section>
  );
};

export default Specs;
