import React from "react";

const Form = ({ title = null, form }) => {
  return (
    <section className="flex max-md:flex-col-reverse bg-slate-50">
      <div>
        <h2 className="title">{title}</h2>
      </div>
      <form>
        {form.map((item, index) => {
          const id = item.label.replace(/\s+/g, "-").toLowerCase();
          return (
            <>
              <label htmlFor={id}>{item.label}</label>
              <input id={id} type="text" className="field" key={index} />
            </>
          );
        })}
      </form>
    </section>
  );
};

export default Form;
