import React from "react";

const Form = ({ title = null, form }) => {
  return (
    <section className="flex max-md:flex-col-reverse">
      <div>
        <h2 className="title">{title}</h2>
      </div>
      <form>
        {form.map((item, index) => {
          return (
            <>
              <label htmlFor={item.label.replace(/\s+/g, "-").toLowerCase()}>
                {item.label}
              </label>
              <input
                id={item.label.replace(/\s+/g, "-").toLowerCase()}
                type="text"
                className="field"
              />
            </>
          );
        })}
      </form>
    </section>
  );
};

export default Form;
