import React from "react";

const Studios = ({ prod }) => {
  return (
    <section className="h-[120vh] padding-x pt-2 bg-fullcenter">
      <div className={`max-w-[700px] ${center && "text-center mx-auto"}`}>
        <Splide
          aria-label={`${car.model} hero slider`}
          options={{
            type: "fade",
            rewind: true,
            autoplay: true,
            arrows: false,
          }}
        >
          {carSlides.map((slide) => {
            return (
              <SplideSlide>
                <section className="min-h-screen w-full bg-fullcenter">
                  <img
                    className="bg-fullobject w-full rounded h-[70vh]"
                    src={image}
                    alt={title}
                  />
                  <div className="mt-10 md:w-3/5 mx-auto">
                    <h3 className="text-2xl font-smibold">{title}</h3>
                    <p className="mt-5">{describe}</p>
                  </div>
                </section>
              </SplideSlide>
            );
          })}

          <div className="absolute w-full text-center top-[90%] z-10 md:text-sm text-[9px]">
            <p>{car.saving}</p>
            <Link className="link">Learn about est. gas savings.</Link>
          </div>
          <div className="md:block hidden splide__progress">
            <div className="splide__progress__bar" />
          </div>
        </Splide>
      </div>
    </section>
  );
};

export default Studios;
