import { Splide, SplideSlide } from "@splidejs/react-splide";

const Studios = ({ prod }) => {
  return (
    <section className="h-[120vh] padding-x pt-2 bg-fullcenter">
      <div className={`max-w-[700px] ${index === 2 && "text-center mx-auto"}`}>
        <div className={`max-w-[700px] ${center && "text-center mx-auto"}`}>
          <h2 className="md:text-3xl text-2xl text-slate-950 font-bold">
            {prod.title}
          </h2>
          <p className="text-sm text-slate-700 pt-6">{prod.describe}</p>
        </div>
        <Splide
          aria-label={`${prod} slider`}
          options={{
            type: "fade",
            rewind: true,
            autoplay: true,
            arrows: false,
          }}
        >
          {prod.slides.map((slide, index) => {
            return (
              <SplideSlide>
                <section
                  className="h-[120vh]"
                  style={{
                    backgroundImage: "url(" + slide.cover + ")",
                  }}
                >
                  <div className="text-center h-3/5 flex flex-col justify-between">
                    <div className="mix-blend-difference">
                      <h3 className="text-xl">{slide.title}</h3>
                      <p>{slide.describe}</p>
                    </div>
                  </div>
                </section>
              </SplideSlide>
            );
          })}

          <div className="absolute w-full text-center top-[90%] z-10 md:text-sm text-[9px]">
            <p>{car.saving}</p>
            <Link className="link">Learn about est. gas savings.</Link>
          </div>
          <div className="md:block hidden splide__progress">
            <div className="splide__progress__bar" />
          </div>
        </Splide>
      </div>
    </section>
  );
};

export default Studios;
