import Button from "./Button";

const Learn_Middle = ({
  title = false,
  topic = false,
  describe = false,
  learnLink = false,
  orderLink = false,
  inventoryLink = false,
  light = false,
  pw = false,
}) => {
  if (title) {
    return (
      <section
        className={`padding-x py-8 flex ${
          light && "bg-slate-50 text-slate-950"
        }`}
      >
        <div className="lg:w-2/5 w-full lg:px-10">
          <h5>{topic}</h5>
          <h2 className="title mt-1 mb-3">{title}</h2>
          <p className="lg:hidden py-5">{describe}</p>
          {!inventoryLink && !orderLink && !learnLink && (
            <div className="md:flex lg:flex-col md:flex-row w-full">
              {inventoryLink &&
                (light ? (
                  <Button
                    to={inventoryLink}
                    label={
                      inventoryLink
                        ? "Veiw Inventory"
                        : learnLink
                        ? "Learn More"
                        : "Order Now"
                    }
                    customClass="sm:max-w-[220px] lg:ms-0 sm:ms-2"
                    light
                  />
                ) : (
                  <Button
                    to={inventoryLink}
                    label={
                      inventoryLink
                        ? "Veiw Inventory"
                        : learnLink
                        ? "Learn More"
                        : "Order Now"
                    }
                    customClass="sm:max-w-[220px] lg:ms-0 sm:ms-2"
                  />
                ))}
            </div>
          )}
        </div>
        <p className="w-3/5 px-10 lg:block hidden">{describe}</p>
      </section>
    );
  }
};

export default Learn_Middle;
