import { Link } from "react-router-dom";
import { navLinks, navTools } from "../../constants";
import { Dropdown } from "../Portal";
import { useState } from "react";
const Navbar = () => {
  const [navOver, setNavOver] = useState(false);
  const [currentItem, setCurrentItem] = useState({});

  useEffect(() => {
    setCurrentItem({ blankItems: Array(5).fill({}) });
  }, []);

  const dropdown = document.querySelectorAll(".dropdownContainer");

  return (
    <header className="absolute w-full z-50">
      <nav
        className={`padding-x py-3 w-full z-10 flex-seperate text-slate-50 relative shadowFromTop transition-all ${
          navOver && "text-slate-950"
        }`}
      >
        <Link to="/" className="z-50">
          <svg
            class="tds-icon tds-icon-logo-wordmark tds-site-logo-icon"
            width={130}
            viewBox="0 0 342 35"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              d="M0.1a9.7 9.7 0 0 0 7 7h11l.5.1v27.6h6.8V7.3L26 7h11a9.8 9.8 0 0 0 7-7H0zm238.6 0h-6.8v34.8H263a9.7 9.7 0 0 0 6-6.8h-30.3V0zm-52.3 6.8c3.6-1 6.6-3.8 7.4-6.9l-38.1.1v20.6h31.1v7.2h-24.4a13.6 13.6 0 0 0-8.7 7h39.9v-21h-31.2v-7h24zm116.2 28h6.7v-14h24.6v14h6.7v-21h-38zM85.3 7h26a9.6 9.6 0 0 0 7.1-7H78.3a9.6 9.6 0 0 0 7 7zm0 13.8h26a9.6 9.6 0 0 0 7.1-7H78.3a9.6 9.6 0 0 0 7 7zm0 14.1h26a9.6 9.6 0 0 0 7.1-7H78.3a9.6 9.6 0 0 0 7 7zM308.5 7h26a9.6 9.6 0 0 0 7-7h-40a9.6 9.6 0 0 0 7 7z"
              fill="currentColor"
            ></path>
          </svg>
        </Link>
        <ul className="lg:flex hidden gap-4 ps-4">
          {navLinks.map((item, index) => {
            return (
              <>
                <li key={index} className="z-50">
                  <Link
                    to={item.href}
                    className={`navitem ${navOver && "hover"}`}
                  >
                    {item.label}
                  </Link>
                </li>
                <div className="dropdownContainer absolute w-full max-h-[500px] top-0 left-0">
                  {currentItem &&
                    (currentItem.label === "DISCOVER" ? (
                      <Dropdown
                        className={
                          navOver
                            ? "top-0 opacity-100"
                            : "top-[-100px]  opacity-0"
                        }
                        links={currentItem.links}
                        discover
                      />
                    ) : (
                      <Dropdown
                        className={
                          navOver
                            ? "top-0 opacity-100"
                            : "top-[-100px]  opacity-0"
                        }
                        prods={currentItem.products}
                        links={currentItem.links}
                      />
                    ))}
                  {currentItem.blankItems.map((item, index) => (
                    <div key={index} className="h-12 bg-white" />
                  ))}
                </div>
              </>
            );
          })}
        </ul>
        <ul className="lg:hidden flex gap-4 ps-4">
          {navTools.map((item, index) => {
            return (
              <li key={index}>
                <Link to={item.href} className="navitem">
                  {item.label}
                </Link>
              </li>
            );
          })}
        </ul>
      </nav>
      <div
        className="w-full h-[1px] bg-slate-200 absolute bottom-0"
        onMouseEnter={() => setNavOver(true)}
        onMouseLeave={() => setNavOver(false)}
      ></div>
    </header>
  );
};

export default Navbar;
