import React from "react";
import { Demo_Models, Field } from "../comps/Portal";
import { demoForm } from "../constants";

const Demo = () => {
  return (
    <section className="max-w-[800px] mx-auto pt-32 padding">
      <h1 className="title">Schedule a Demo Drive</h1>
      <p className="text-xs my-3">Demo Drive a Tesla at a store near you.</p>
      <Demo_Models />
      <div className="my-24">
        <h2 className="title">Find Time and Location</h2>
        <p className="my-6">
          Input your postal code to search for Tesla showrooms and schedule a
          Test Drive
        </p>
        <div className="max-w-[320px]">
          <Field
            icon="fa-magnifying-glass"
            id="postalCode"
            placeholder="Postal Code"
          />
        </div>
      </div>

      <div className="my-24">
        {demoForm.map((item, index) => {
          const id = item.label.replace(/\s+/g, "-").toLowerCase();
          return (
            <div key={index}>
              <label
                htmlFor={id}
                className="text-slate-200 md:text-sm text-xs font-bold"
              >
                {item.label}
              </label>
              <div className="mt-1">
                {item.type === "textarea" ? (
                  <textarea className="field min-h-[120px]"></textarea>
                ) : item.type === "select" ? (
                  <select className="field">
                    {item.options.map((option, index) => {
                      let isSelected = false;
                      if (item.label === "Contact Preference") {
                        if (isMobile) {
                          isSelected = index === 0;
                        } else {
                          isSelected = index === 1;
                        }
                      }
                      return (
                        <option key={index} selected={isSelected}>
                          {option}
                        </option>
                      );
                    })}
                  </select>
                ) : (
                  <input id={id} type={item.type} className="field" />
                )}
              </div>
            </div>
          );
        })}
      </div>
    </section>
  );
};

export default Demo;
