import { Link } from "react-router-dom";
import { navLinks, navTools } from "../constants";
import Dropdown from "./Dropdown";
import { useState, useRef, useEffect } from "react";
import { createStyles, makeStyles } from "@mui/material";

const useStyles = makeStyles(() =>
  createStyles({
    dropdownContainer: {
      position: "absolute",
      top: "-500px",
      left: 0,
      width: "100%",
      transition: "top 0.3s ease-in-out",
    },
  })
);

const Navbar = () => {
  const [navOver, setNavOver] = useState(false);
  const dropdownRef = useRef(null);
  const classes = useStyles();

  const handleMouseOver = () => {
    setNavOver(true);
  };

  const handleMouseOut = (event) => {
    if (!event.relatedTarget || !event.relatedTarget.closest("nav")) {
      setNavOver(false);
    }
  };

  useEffect(() => {
    if (navOver) {
      dropdownRef.current.style.top = "50px";
    } else {
      dropdownRef.current.style.top = "-500px";
    }
  }, [navOver]);

  return (
    <header className="absolute w-full z-50">
      <nav
        className={`padding-x py-3 w-full z-10 flex-seperate text-slate-50 relative ${
          navOver && "text-slate-950 bg-slate-50"
        }`}
        onMouseOut={handleMouseOut}
      >
        <Link to="/">
          <svg
            class="tds-icon tds-icon-logo-wordmark tds-site-logo-icon"
            width={130}
            viewBox="0 0 342 35"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              d="M0.1a9.7 9.7 0 0 0 7 7h11l.5.1v27.6h6.8V7.3L26 7h11a9.8 9.8 0 0 0 7-7H0zm238.6 0h-6.8v34.8H263a9.7 9.7 0 0 0 6-6.8h-30.3V0zm-52.3 6.8c3.6-1 6.6-3.8 7.4-6.9l-38.1.1v20.6h31.1v7.2h-24.4a13.6 13.6 0 0 0-8.7 7h39.9v-21h-31.2v-7h24zm116.2 28h6.7v-14h24.6v14h6.7v-21h-38zM85.3 7h26a9.6 9.6 0 0 0 7.1-7H78.3a9.6 9.6 0 0 0 7 7zm0 13.8h26a9.6 9.6 0 0 0 7.1-7H78.3a9.6 9.6 0 0 0 7 7zm0 14.1h26a9.6 9.6 0 0 0 7.1-7H78.3a9.6 9.6 0 0 0 7 7zM308.5 7h26a9.6 9.6 0 0 0 7-7h-40a9.6 9.6 0 0 0 7 7z"
              fill="currentColor"
            ></path>
          </svg>
        </Link>
        <ul className="lg:flex hidden gap-4">
          {navLinks.map((item, index) => {
            return (
              <>
                <li key={index}>
                  <Link
                    to={item.href}
                    className={`navitem ${navOver && "hover"}`}
                    onMouseOver={handleMouseOver}
                  >
                    {item.label}
                  </Link>
                </li>
                <div
                  className={`dropdownContainer ${classes.dropdownContainer}`}
                  ref={dropdownRef}
                >
                  {navOver && (
                    <Dropdown
                      className={navOver ? "top-[50px]" : "top-[-500px]"}
                      prods={item.products}
                      links={item.links}
                    />
                  )}
                </div>
              </>
            );
          })}
        </ul>
        <ul className="lg:flex hidden gap-4">
          {navTools.map((item, index) => {
            return (
              <li key={index}>
                <Link
                  to={item.href}
                  className={`navitem ${navOver && "hover"}`}
                  onMouseOver={handleMouseOver}
                >
                  {item.label}
                </Link>
              </li>
            );
          })}
        </ul>
      </nav>
    </header>
  );
};

export default Navbar;
