import React from "react";
import { Link } from "react-router-dom";
import { tradeinForm } from "../../constants";

const Tradein_Form = () => {
  return (
    <section className="max-w-[800px] mx-auto pt-32">
      <p>
        <i className="fal fa-chevron-left" />
        <Link to="/" className="link ms-2">
          Back
        </Link>
      </p>
      <h1 className="title mt-2">Vehicle Details</h1>
      <div className="mt-12 md:flex gap-5">
        {tradeinForm.map((item, index) => {
          return (
            <div className={`md:w-1/2`}>
              const id = item.label.replace(/\s+/g, "-").toLowerCase(); return (
              <label
                htmlFor={id}
                className="text-slate-200 md:text-sm text-xs font-bold"
              >
                {item.label}
              </label>
              <div className="mt-1">
                {item.type === "textarea" ? (
                  <textarea className="field min-h-[120px]"></textarea>
                ) : item.type === "select" ? (
                  <select className="field">
                    {item.options.map((option, index) => {
                      let isSelected = false;
                      if (item.label === "Contact Preference") {
                        if (isMobile) {
                          isSelected = index === 0;
                        } else {
                          isSelected = index === 1;
                        }
                      }
                      return (
                        <option key={index} selected={isSelected}>
                          {option}
                        </option>
                      );
                    })}
                  </select>
                ) : (
                  <input id={id} type={item.type} className="field" />
                )}
              </div>
              );
            </div>
          );
        })}
      </div>
    </section>
  );
};

export default Tradein_Form;
