import { Link } from "react-router-dom";

const Dropdown = ({ prods = false, links = false, discover = false }) => {
  return (
    <section className="fixed top-0 w-screen bg-slate-50 text-slate-950 padding pt-24 z-40">
      <div className="flex">
        <div className="w-3/4 grid grid-cols-3 gap-5">
          {prods.map((item, index) => {
            return (
              <div className="w-1/3" key={index}>
                <img src={item.cover} alt={item.title} />
                <h4 className="font-bold text-center text-nowrap">
                  {item.title}
                </h4>
                <p className="text-sm flex justify-between">
                  {item.links.map((link, index) => {
                    <Link to={link.to} key={index}>
                      {link.label}
                    </Link>;
                  })}
                </p>
              </div>
            );
          })}
        </div>
        <div className="w-1/4">
          <ul>
            {links.map((item, index) => {
              return (
                <li key={index}>
                  <Link to={item.to}>{item.label}</Link>
                </li>
              );
            })}
          </ul>
        </div>
      </div>
    </section>
  );
};

export default Dropdown;
