import React from "react";
import { vehicles } from "../../../constants";

const Learn_Amenities = (props) => {
  const { car } = props;
  const carAmenities = vehicles.find(
    (vehicle) => vehicle.model === car.model
  ).amenities;

  return (
    <section className="padding">
      {carAmenities.map((item) => {
        return (
          <div className="flex justify-between md:flex-row flex-col padding-x py-5">
            {item.image.endsWith(".mp4") || item.image.endsWith(".webm") ? (
              <video autoPlay muted loop className="md:w-1/2 h-96">
                <source
                  src={item.image}
                  type={`video/${item.image.split(".").pop()}`}
                />
                Your browser does not support the video tag.
              </video>
            ) : (
              <img
                src={item.image}
                alt={item.title}
                className="md:w-1/2 h-96"
              />
            )}
            <div className="md:w-1/2 flex items-center">
              <div className="md:padding">
                <h3>{item.title}</h3>
                <p>{item.describe}</p>
              </div>
            </div>
          </div>
        );
      })}
    </section>
  );
};

export default Learn_Amenities;
