import React, { useEffect, useState } from "react";

const Home = () => {
    const [isDark, setDark] = useState(false);

    const toggleDarkMode = () => {
        setDark(!isDark);
    };

    useEffect(() => {
        if (isDark) {
            document.documentElement.classList.add('dark');
        } else {
            document.documentElement.classList.remove('dark');
        }
    }, [isDark]);


    return (
        <main className="p-5">
            <h1>
                Hello world!
            </h1>
            <ul >
                <li className="first:bg-green-500 first:text-white">Yoda</li>
                <li className="first:bg-green-200 odd:bg-blue-600 odd:text-white even:bg-red-600">Sith</li>
                <li className="first:bg-green-200 odd:bg-blue-600 odd:text-white even:bg-red-600">Jedi</li>
                <li className="first:bg-green-200 odd:bg-blue-600 odd:text-white even:bg-red-600">Sith</li>
                <li className="first:bg-green-200 odd:bg-blue-600 odd:text-white even:bg-red-600">Jedi</li>
            </ul>

            <div className="card">
                <h4>May the Force be with you</h4>
                <p>The darkside of the force is the pathway to many abilities that consider to be unnatural...</p>
                <button id="darkTheme" onClick={toggleDarkMode}>Dark</button>
            </div>
        </main>
    );
};

export default Home;
